import React, { useState } from 'react';

const Calculator = () => {
  const [input, setInput] = useState(''); // State to store the input

  const handleClick = (value) => {
    setInput(input + value); // Append clicked button value to input
  };

  const handleClear = () => {
    setInput(''); // Clear input
  };

  const handleEquals = () => {
    try {
      setInput(eval(input).toString()); // Evaluate the expression
    } catch (e) {
      setInput('Error'); // If there's an error in the expression
    }
  };

  return (
    <div style={{ maxWidth: '200px', margin: '0 auto' }}>
      <div style={{ marginBottom: '10px', padding: '10px', border: '1px solid #ccc' }}>
        {input}
      </div>
      <div>
        {[1, 2, 3].map(num => (
          <button key={num} onClick={() => handleClick(num.toString())}>{num}</button>
        ))}
        <button onClick={() => handleClick('+')}>+</button>
      </div>
      <div>
        {[4, 5, 6].map(num => (
          <button key={num} onClick={() => handleClick(num.toString())}>{num}</button>
        ))}
        <button onClick={() => handleClick('-')}>-</button>
      </div>
      <div>
        {[7, 8, 9].map(num => (
          <button key={num} onClick={() => handleClick(num.toString())}>{num}</button>
        ))}
        <button onClick={() => handleClick('*')}>*</button>
      </div>
      <div>
        <button onClick={handleClear}>C</button>
        <button onClick={() => handleClick('0')}>0</button>
        <button onClick={handleEquals}>=</button>
        <button onClick={() => handleClick('/')}>/</button>
      </div>
    </div>
  );
};

export default Calculator;
