import React, { useState } from "react";


const ItemAdd=()=>{
    const[item,setitem]=useState([]);
    const[value,setvalue]=useState(""); 

    const addItem=()=>{
   if(value.trim()!==""){
    setitem([...item,value.trim()])
    setvalue("");

   }
    }

    function handler(e){
        setvalue(e.target.value);
    }
    return(
        <div>
      <input type="text"
      value={value}
      onChange={handler}
      placeholder="addd the list"
      />
      <ul>{
        item.map((itemm,index)=>
        <li key={index}>{itemm}</li>)
        }
      </ul>
        </div>
    )
}

export default ItemAdd;
